    private void jMenuItem1ActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jMenuItem1ActionPerformed
        // TODO add your handling code here:
        ListaPedidos lista_pedidos;
        lista_pedidos = new ListaPedidos();
        lista_pedidos.setVisible(true);
        lista_pedidos.exibeTodos(todos_pedidos);
    }//GEN-LAST:event_jMenuItem1ActionPerformed

    private void jButton1ActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jButton1ActionPerformed

        if (pizzas.size() > 0) {
            pedido = new Pedido(cliente, pizzas); //completar
            cliente = new Cliente("", "", "");
            pizzas.clear();
            jTextArea1.setText("");
            jTextField1.setText("");
            jTextField2.setText("");
            jTextField3.setText("");
            todos_pedidos.add(pedido);
            JOptionPane.showMessageDialog(null, "Pedido número: " + todos_pedidos.size(), "Número do Pedido", 1);
        } else {
            JOptionPane.showMessageDialog(null, "Nenhuma pizza foi pedida", "ERRO", 0);
        }
        // TODO
   
    }//GEN-LAST:event_jButton1ActionPerformed

    private void jButton3ActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jButton3ActionPerformed
        // este método está pronto!
        obterDadosCliente();
        
        Object selecao = jComboBox1.getSelectedItem();
        String tam = selecao.toString();
        
        pizzas.add(new FrangoCatupiry(tam));
        pizzas.get(pizzas.size()-1).montarPizza();
        
        exibirDadosTela();     
    }//GEN-LAST:event_jButton3ActionPerformed

