    private void mostraIngredientes(Pizza p) {
        String str = "Ingredientes\n\n";
        for (int i = 0; i < p.ingredientes.size(); i++) {
            str += p.ingredientes.get(i) + "\n";
        }
        JOptionPane.showMessageDialog(null, str, p.getNome(), 1);
    }
    
