    private void obterDadosCliente(){
        // este método está pronto!
        String nome = jTextField1.getText();
        String endereco = jTextField2.getText();
        String tel = jTextField3.getText();
        cliente = new Cliente(nome, endereco, tel);
    }
    
    private void exibirDadosTela(){
       // TODO
       if (pizzas.size() == 1) {
           jTextArea1.append(cliente.getNome()+" "+cliente.getEndereco()+" "+cliente.getTelefone()+"\n");
       }
       Pizza p = pizzas.get(pizzas.size()-1);
       jTextArea1.append(p.getNome() + " " + p.getTamanho() + " " + p.getPreco() + "\n");
    }
    
    
